body.script:
document.addEventListener('DOMContentLoaded', () => {
    const downloadBtn1 = document.getElementById('download-btn');
    const downloadBtn2 = document.getElementById('download-btn-2');
    const wishlistModal = document.getElementById('wishlist-modal');
    const closeWishlistModalBtn = document.getElementById('close-wishlist-modal-btn');

    const contactLink = document.getElementById('contact-link');
    const contactModal = document.getElementById('contact-modal');
    const closeContactModalBtn = document.getElementById('close-contact-modal-btn');

    const showModal = (modal) => {
        modal.classList.remove('hidden');
    };

    const hideModal = (modal) => {
        modal.classList.add('hidden');
    };

    downloadBtn1.addEventListener('click', () => showModal(wishlistModal));
    downloadBtn2.addEventListener('click', () => showModal(wishlistModal));
    closeWishlistModalBtn.addEventListener('click', () => hideModal(wishlistModal));

    wishlistModal.addEventListener('click', (e) => {
        if (e.target === wishlistModal) {
            hideModal(wishlistModal);
        }
    });

    contactLink.addEventListener('click', (e) => {
        e.preventDefault();
        showModal(contactModal);
    });
    closeContactModalBtn.addEventListener('click', () => hideModal(contactModal));

    contactModal.addEventListener('click', (e) => {
        if (e.target === contactModal) {
            hideModal(contactModal);
        }
    });
});
